mapper.select:
select t1.*,t2.item_name
from xm_wb_rlpp left join jl_project t2 on t1.project_id = t2.id
where t1.id = #{value}

		select t1.*,t2.item_name
		from xm_wb_rlpp t1 left join jl_project t2 on t1.project_id = t2.id

		  		   and t1.id = #{id} 
		  		   and t1.project_id = #{projectId} 
		  		   and t1.compact = #{compact} 
		  		   and t1.end_date = #{endDate} 
		  		   and t1.date = #{date} 
		  		   and t1.count = #{count} 
		  		   and t1.user_id = #{userId} 
		  		   and t1.month_sum = #{monthSum} 
			       and t2.item_name = #{itemName} 



                order by ${sort} ${order}


                order by t1.id desc



			limit #{offset}, #{limit}

select count(*) from xm_wb_rlpp

  		   and id = #{id} 
  		   and project_id = #{projectId} 
  		   and compact = #{compact} 
  		   and end_date = #{endDate} 
  		   and date = #{date} 
  		   and count = #{count} 
  		   and user_id = #{userId} 
  		   and month_sum = #{monthSum}
